import React, { useState, useEffect, useRef } from 'react'
import axios from 'axios'

import { Card, Button, Table, Space, Image, Switch, Popconfirm, Tag, Input, Modal, message, Spin } from 'antd';
import { EditOutlined, EyeOutlined, DeleteOutlined, QuestionCircleOutlined, UploadOutlined } from '@ant-design/icons'

import { useSelector } from 'react-redux'

import moment from 'moment';
const Search = Input.Search;

function Department({ type }) {
    const { user } = useSelector(state => state.auth)
    const [isUploading, setIsUplaoding] = useState(false)
    const [isLoading, setIsLoading] = useState(false)
    const [departments, setDepartments] = useState([])
    const [records, setRecords] = useState([])
    const [visible, setVisible] = useState(false)
    const [selected, setSelected] = useState(null)

    const [name, setName] = useState("")
    const [image, setImage] = useState("")
    const [isFeatured, setIsFeatured] = useState("")

    const [page, setPage] = useState(1);


    const onEditClick = (department) => {
        setVisible(true)
        setSelected(department)
        setName(department.name)
        setImage(department.image)
        setIsFeatured(department.isFeatured)
    }
    const onCancelModal = () => {
        setVisible(false)
        setSelected(null)
        setName("")
        setImage("")
    }

    useEffect(() => {
        setIsLoading(true)
        axios.get(`/doctor/department/get`)
            .then(res => {
                setDepartments(res.data.departments);
                setRecords(res.data.departments);
                setIsLoading(false)
            })
            .catch(err => {
                setIsLoading(false)
                console.log(err);
            })
    }, [])


    const handleSearch = val => {

        if (val == "")
            setDepartments(records);
        else
            setDepartments(records.filter(x => x.name.includes(val)))

    }

    const handleSave = () => {
        let data = {
            name,
            image,
            isFeatured
        }

        if (selected) {
            axios.patch('/doctor/department/edit/' + selected._id, data)
            .then(res => {
                if (res.data.success) {
                    let temp = [...records]
                    let index = temp.findIndex(user => user._id === selected._id)
                    temp[index] = res.data.department
                    setRecords(temp)
                    setDepartments(temp)
                    onCancelModal()
                }
            })
            .catch(err => {
                err && err.response && alert(err.response.data.error)
            })
        } else {
            

                axios.post('/doctor/department/create', data)
                .then(res => {
                    setDepartments(prev => ([...prev, res.data.department]))
                    onCancelModal()
                })
                .catch(err => {
                    err && err.response && alert(err.response.data.error)
                })
        }

    }

const deleteItem=(id)=>{
    axios.delete(`/doctor/department/delete/${id}`)
    .then(res=>{
        setDepartments(prev=>prev.filter(i=>i._id !== id))
    })
    .catch(err => {
        err && err.response && alert(err.response.data.error)
    })
}

    const columns = [
        {
            title: 'Index',
            key: 'sno',
            width:"5%",
            render: (text, object, index) => (page - 1) * 10 + index + 1

        },

        {
            title: 'Image',
            dataIndex: 'image',
            key: 'image',
            render: image => <Image
                width={60}

                src={image ? image : "data:image/png;base64,"}
                fallback="data:image/png;base64,"
            />
        },
        {
            title: 'Name',
            dataIndex: 'name',
            key: 'name',
           
        },
        {
            title: 'Featured',
            dataIndex: 'isFeatured',
            key: 'isFeatured',
            render:isFeatured=>(
                isFeatured ? <Tag color='green'>Featured</Tag>:<Tag>Not featured</Tag>
            )
           
        },
        {
            title: 'Action',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    <Button disabled={record.role === 'admin' || user.role !== 'admin'} onClick={() => onEditClick(record)} className='d-center' type='primary' icon={<EditOutlined />}></Button>
                   
                    <Popconfirm onConfirm={() => deleteItem(record._id)} title="Are you sure？" icon={<QuestionCircleOutlined style={{ color: 'red' }} />}>
                        <Button className='d-center' icon={<DeleteOutlined />} danger></Button>
                    </Popconfirm> 

                </Space>
            ),
        },
    ];



    let imageRef = useRef()


    const handleUpload = (files) => {
        if (files) {
            setIsUplaoding(true)
            let formData = new FormData()
            formData.append("image", files)
            axios.post('/general/image', formData)
                .then(res => {
                    setImage(res.data.image)

                    setIsUplaoding(false)

                })
                .catch(err => {
                    imageRef.current.value = null
                    setIsUplaoding(false)
                    console.log(err);
                    message.error("Upload failed")
                })
        }
    }

    return (
        <Card title={
            <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <span>All departments</span>
                <Button onClick={() => setVisible(true)} type='primary'>Add</Button>
            </div>
        }>
            <Search
                placeholder="Enter name"
                onSearch={handleSearch}
                style={{ width: 200 }}
            />
            <Table
                rowKey="_id"
                // rowSelection={rowSelection}
                loading={isLoading}
                columns={columns}
                dataSource={departments}
                pagination={{ defaultPageSize: 10, showSizeChanger: true, pageSizeOptions: ['10', '20', '30'], showQuickJumper: true }}
            />






            <Modal
                title={selected ? selected.name : "Add department"}
                centered
                visible={visible}
                onOk={() => handleSave()}
                onCancel={() => onCancelModal()}
                zIndex={1111}
                okText={"Save"}
            >

                <label htmlFor="name">Department Name</label>
                <Input value={name} onChange={e => setName(e.target.value)} />

                <label style={{ padding: "5px 10px", border: "1px solid #000", display: "inline-block", margin: "10px 0" }} htmlFor="image" >Upload image <UploadOutlined className='icon' /> {isUploading && <Spin style={{ marginLeft: "10px" }} />}</label>
                <input ref={imageRef} onChange={(e) => handleUpload(e.target.files[0])} style={{ display: "none" }} id='image' type="file" />
                <br />
                {
                    image && <Image src={image} style={{ maxWidth: "150px" }} />
                }
                <br/>
                <label htmlFor="featured">Featured :</label>
                <Switch  checked={isFeatured} onChange={val=>setIsFeatured(val)} />
            </Modal>
        </Card>
    )
}

export default Department
